import React, {useState} from 'react';
import {FontAwesomeIcon, FontAwesomrIcon} from '@fortawesome/react-fontawesome';
import {faPlay, faAngleLeft, faAngleRight, faPause} from '@fortawesome/free-solid-svg-icons';

const Player = ({songInfo, setSongInfo, currentSong, isPlaying, setIsPlaying,audioRef}) => {
// Ref

 // Event handlers
const playSongHandler= () => {
    if(isPlaying){
        audioRef.current.pause();
        setIsPlaying(!isPlaying);
    } else{
        audioRef.current.play();
        setIsPlaying(!isPlaying);
    }
};
    // state
    // const [songInfo, setSongInfo] = useState({
    //     currentTime: 0,
    //     duration: 0,
    // });

    // const timeUpdateHandler = (e) =>{
    //     const current = e.target.currentTime;
    //     const duration = e.target.duration;
    //     setSongInfo({...songInfo, currentTime:current, duration});
    // };

    const getTime = (time) => {
    return(
        Math.floor(time / 60) +":" + ("0" + Math.floor((time) % 60)).slice(-2));
    };

    const dragHandler= (e) => {
        audioRef.current.currentTime = e.target.value;
        setSongInfo({...songInfo, currentTime:e.target.value});
    }

    return(
        <div className="player">
            <div className="time-control">
                <p>{getTime(songInfo.currentTime)}</p>
                <input onChange={dragHandler} min={0} max={songInfo.duration} value={songInfo.currentTime} type="range" />
                <p>{getTime(songInfo.duration)}</p>
            </div>
            <div className="play-control">
                <FontAwesomeIcon className="skip-back" size='2x' icon={faAngleLeft}/>
                <FontAwesomeIcon onClick={playSongHandler} className="play" size='2x' icon={isPlaying ? faPause : faPlay}/>
                <FontAwesomeIcon className="skip-forward" size='2x' icon={faAngleRight}/>
            </div>
            {/* <audio onTimeUpdate={timeUpdateHandler} onLoadedMetadata={timeUpdateHandler} ref={audioRef} src={currentSong.audio}></audio> */}
        </div>
    )
}

export default Player;
